function submitContactForm(){var t=$("#contact"),n=[],e=t.find(".questions .question-container");e.each(function(){var t=$(this).find(".answer"),e=t.attr("placeholder"),i=t.val();n.push({question:e,answer:i})}),$.ajax({url:"https://litebyteus.ipage.com/util/functions.php",type:"POST",data:{operation:"submitContactForm",data:JSON.stringify(n)},success:function(){t.find(".submit").prop("value","Sent!")}})}function disableContactForm(){$("#contact .submit").prop("disabled",!0)}function adjustTextArea(t){t.style.height="1px",t.style.height=118+t.scrollHeight+"px"}function initMap(){var t=new google.maps.Map(document.getElementById("map"),{center:{lat:-34.5883534,lng:-58.4298406},scrollwheel:!1,zoom:16});new google.maps.Marker({map:t,position:{lat:-34.5883534,lng:-58.4298406},title:"We are here!"})}$(document).ready(function(){$("#contact .submit").click(function(){disableContactForm(),submitContactForm()})}),$(document).ready(function(){var t=$("body");t.find(".animate").each(function(t,n){var e=$(n).find("img"),i=0;e.css("transition","opacity 100ms"),setInterval(function(){e.eq(i).css({opacity:1}),setTimeout(function(){e.eq(i-1).css({opacity:0}),i++,i>e.length-1&&(i=0)},100)},750)})});